<?phpadd_action( 'wp_enqueue_scripts', 'child_enqueue_styles',99);function child_enqueue_styles() {    $parent_style = 'parent-style';    wp_enqueue_style( $parent_style, get_template_directory_uri() . '/style.css' );	 wp_enqueue_style( 'child-style',get_stylesheet_directory_uri() . '/custom.css', array( $parent_style ));}if ( get_stylesheet() !== get_template() ) {    add_filter( 'pre_update_option_theme_mods_' . get_stylesheet(), function ( $value, $old_value ) {         update_option( 'theme_mods_' . get_template(), $value );         return $old_value; // prevent update to child theme mods    }, 10, 2 );    add_filter( 'pre_option_theme_mods_' . get_stylesheet(), function ( $default ) {        return get_option( 'theme_mods_' . get_template(), $default );    } );}//[faq_category_name]function faq_category_name() {	$sqluser   = "user";	$sqlpass   = 'pass';	$sqlhost   = "host";	$sqldb   = "dbname";	$connection = mysql_connect($sqlhost, $sqluser, $sqlpass);	if( !$connection ) {		die('Could not connect to Database: '.mysql_error());	}	$db = mysql_select_db($sqldb,$connection);	$query = "select name from `wpus_terms` join `wpus_term_taxonomy` on `wpus_terms`.term_id = `wpus_term_taxonomy`.term_id where taxonomy = 'ufaq-category'";	$daftar = mysql_query($query,$connection) or die("Failed to pull category name list = ".$query);		$category_list = "<option>All FAQ</option>";		while ($buffer = mysql_fetch_array($daftar,MYSQL_BOTH)) {$result[$c]=$buffer;$c++;};		while (list($key, $val)=each($result)) {		$category_list .= "<option>$val[name]</option>";	}		mysql_close($connection);	return  $category_list;}add_shortcode( 'faq_category_name', 'faq_category_name' );function unread_posts_count() {	/* code to add an unread value to the proper table. */	if( $post_status == 'publish' ) {		$mySQLuser   = "user";		$mySQLpass   = 'pass';		$mySQLhost   = "host";		$mySQLdb   = "dbname";		$connection = mysql_connect($mySQLhost, $mySQLuser, 